        // Textbox 1 ChangeText
        private void textBox1_TextChanged(object sender, EventArgs e)
        {
            int zahl = 0;
            int zahl2 = 0;
            if (int.TryParse(textBox1.Text, out zahl) && int.TryParse(textBox2.Text, out zahl2))
            {
                button1.Text = "Gültig";
                button2.Text = "Gültig";
                button1.BackColor = Color.Green;
                button2.BackColor = Color.Green;
            }
            else
            {
                button1.Text = "Ungültig";
                button1.BackColor = Color.Red;
                button2.BackColor = Color.Red;
                button2.Text = "Ungültig";
            }
        }

        /// <summary>
        /// Button Klick GGT
        /// </summary>
        /// <param name="sender"></param>
        /// <param name="e"></param>
        private void button1_Click(object sender, EventArgs e)
        {
            int rechnung = 0;
          
            if (button1.Text == "Gültig" && button2.Text == "Gültig")
            {
                int box1 = int.Parse(textBox1.Text);
                int box2 = int.Parse(textBox2.Text);
                rechnung = GGTBerechnen(box1,box2);

                label1.BackColor = Color.Green;
                label1.Left = 100;
                label1.Text = "Der GGT von "+ box1.ToString() + " und " + box2.ToString() + " lautet "+  rechnung.ToString();
               
            }
            else
            {
                label1.Left = 100;
                label1.BackColor = Color.Red;
                label1.Text = "ich kann nur mit Zahlen rechnen";
            }
           
        }

        //ButtonKlick KGV
        private void button2_Click(object sender, EventArgs e)
        {
            int rechnung = 0;
            if (button1.Text == "Gültig" && button2.Text == "Gültig")
