        private void button4_Click(object sender, EventArgs e)
        {
            int counter = 0;
            if (textBox5.Text.Length == 1)
            {
                for (int i = 0; i < textBox4.Text.Length; i++)
                {
                    if (textBox5.Text.ToLower() == textBox4.Text[i].ToString().ToLower())
                    {
                        counter++;
                    }
                }
                label3.Text = "Die Häufigkeit von " + textBox5.Text + " ist " + counter;
            }
            else
            {
                label3.Text = "Wie wäre es mit nur einem Zeichen??";
            }
        }


