                button3.BackColor = Color.Red;
            }
            else
            {
                button3.BackColor = Color.Green;
            }

        }



        /// <summary>
        /// Diese Funktion berechnet den GrösstenGemeinsamenTeiler
        /// </summary>
        /// <param name="zahl1"></param>
        /// <param name="zahl2"></param>
        /// <returns></returns>
        static int GGTBerechnen(int zahl1, int zahl2)
        {

            if (zahl2 == 0)
            {
                return zahl1;
            }
            else
            {
                return GGTBerechnen(zahl2, zahl1 % zahl2);
            }
        }
        /// <summary>
        /// Diese Funktion berechnet das kleinste gemeinsame Vielfache
        /// </summary>
        /// <param name="a"></param>
        /// <param name="b"></param>
        /// <returns></returns>
        static int KGVBerechnen(int a, int b)
        {
            int big;
            if (a == b)
            { return a; }
            else if (a > b)
            {
                big = a;
            }
            else
            {
                big = b;
            }
            for (int i = big; i < a * b; i++)
            {
                if (i % a == 0 && i % b == 0)
                { return i; }
            }
            return a * b;
        }

